mapper.select:
SELECT COUNT(*)
FROM TRAINEE
WHERE TR_NAME=#{tr_name}

mapper.update:
UPDATE TRAINEE SET TR_PIC=#{tr_pic},
TR_NAME=#{tr_name},
TR_BRANCH1=#{tr_branch1},
TR_BRANCH2=#{tr_branch2},
TR_CONTENT=#{tr_content}
WHERE MEM_ID=#{mem_id}

UPDATE ACTIVITY SET
AC_TITLE=#{ac_title},
AC_CONTENT=#{ac_content},
AC_FILE=#{ac_file}
WHERE
AC_NO=#{ac_no}

mapper.select:
SELECT COUNT(*)
FROM ACTIVITY
WHERE MEM_ID = #{mem_id}

mapper.update:
UPDATE ACTIVITY 
SET AC_HITS = AC_HITS+1 
WHERE AC_NO=#{ac_no}
