mapper.update:
UPDATE MEMBER SET MEM_PW = #{mem_pw}, MEM_PHONE = #{mem_phone},
MEM_PIC = #{mem_pic},
MEM_EMAIL = #{mem_email}, MEM_ADDRESS = #{mem_address}, MEM_ADDRESS2 = #{mem_address2}, MEM_ZIPADDRESS = #{mem_zipAddress} WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT * FROM ( SELECT A.*,
ROWNUM RN FROM (
SELECT * FROM LETTER Where mem_id = #{mem_id} ORDER BY
lett_no
) A ) B WHERE RN BETWEEN #{lett_first} AND #{lett_last}

SELECT * FROM ( SELECT A.*,
ROWNUM RN FROM (
SELECT * FROM QNA Where mem_id = #{mem_id} ORDER BY
q_no
) A ) B WHERE RN BETWEEN #{q_first} AND #{q_last}
